import React, { useState, useEffect } from 'react'
import DisplayPerson from './components/Persons'
import FilterPersons from './components/Filter'
import AddPerson from './components/AddPerson'
import personService from './services/persons'



const App = () => {

  const [persons, setPersons] = useState([''])
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber ] = useState('')
  const [ filter, setNewFilter  ] = useState('')
  const [notification, setNotification] = useState(null)
  const [error, setError] = useState(null)

  useEffect(() => {
    personService
      .getAll()
      .then(response => setPersons(response))
      .catch(error => console.log(error))
  }, [])

  const showMessage = (message, error) => {
    setNotification(message)
    setError(error)

    setTimeout(() => {
      setNotification(null)
      setError(null)
    }, 5000)
  }

  const addPerson = event => {
    event.preventDefault()

    const person = {
        name: newName,
        number : newNumber
    }

    const find_person = persons.find(person => person.name.toLowerCase() === newName.toLowerCase())

    if (find_person) {
      alert(`${newName} is already added to phonebook. Should the name be replaced?`)
      personService
        .update(find_person.id, person)
        .then(response => {
          setPersons(persons
            .filter(p => p.name !== person.name)
            .concat(response)
          )
          setNewName('')
          setNewNumber('')
          showMessage(`PÃ¤ivitettiin ${person.name}`)
        })
        .catch(error => {
          showMessage(error.response.data.error.message, true)
        })
    }
    else {
      personService
        .create(person)
        .then(response => {
          setPersons(persons.concat(person))
          setNewName('')
          setNewNumber('')
          showMessage(`${person.name} was added to yours phonenook`)
        })
        .catch(error => {
          showMessage(error.response.data.error.message, true)
        })
    }
  }

  const removePerson = (event, name) => {
    event.preventDefault()
    const person = persons.find(person => person.name === name)
    if (person) {
      if (window.confirm(`Should ${person.name} be removed`)) {
        personService
          .remove(person.id)
          .then(response => {
            setPersons(persons.filter(p => p.id !== person.id))
            showMessage(`${person.name} was removed`)
          })
          .catch(error => {
            showMessage(`${person.name} doesn't exist in phonebook`, true)
            setPersons(persons.filter(p => p.id !== person.id))
            console.log(error)
          })
      }
    }
  }

  const filteredPersons = filter
    ? persons.filter(person => { 
      return person.name.toLowerCase().includes(filter.toLowerCase())
    })
    : persons



  return (
    <div>
      <h2>Phonebook</h2>
      <FilterPersons setFilter = {setNewFilter} filter = {filter}/>
      <h2>Add new contact</h2>
      <AddPerson 
      addPerson = {addPerson}
      setNewName = {setNewName}
      setNewNumber = {setNewNumber}
      newName = {newName}
      newNumber = {newNumber}
      />  
      <h2>Contacts</h2>
      <ul>
      {filteredPersons.map((person, i) =>
      <DisplayPerson persons={filteredPersons} removePerson={removePerson} />
      )}
      </ul>
    </div>
  )

}

export default App
