import React, { useState, useEffect } from 'react'
import DisplayPersons from './components/Persons'
import FilterPersons from './components/Filter'
import AddPerson from './components/AddPerson'
import axios from 'axios'


const App = () => {

  const [persons, setPersons] = useState([''])
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber ] = useState('')
  const [ filter, setNewFilter  ] = useState('')

  const getPersons = async () => {
    const response = await axios.get('http://localhost:3001/persons')
    setPersons(response.data)
  }

  useEffect(() => {
    getPersons()
  }, [])

  const addPerson = event => {
    event.preventDefault()

    const person = {
        name: newName,
        number : newNumber
    }

    if (persons.find(person => person.name.toLowerCase() === newName.toLowerCase())) {
      alert(`${newName} is already added to phoneboo  `)
    } 
    else {
      setPersons(persons.concat(person))
      setNewName('')
      setNewNumber('')
    }
  }


  const filteredPersons = filter
  ? persons.filter(person => {
    return person.name.toLowerCase().includes(filter.toLowerCase())
  })
  : persons



  return (
    <div>
      <h2>Phonebook</h2>
      <FilterPersons setFilter = {setNewFilter} filter = {filter}/>
      <h2>Add new contact</h2>
      <AddPerson 
      addPerson = {addPerson}
      setNewName = {setNewName}
      newName = {newName}
      setNewNumber = {setNewNumber}
      newNumber = {newNumber}
      />  
      <h2>Contacts</h2>
      <ul>
      {persons.map((persons, i))}
      <DisplayPerson key = {persons.name} person = {filteredPersons} />
      </ul>
    </div>
  )

}

export default App
