import React, { useState, useEffect } from 'react'
import axios from 'axios'
import AddPerson from "./components/AddPerson"
import ShowPersons from "./components/Persons"
import FilterPersons from"./components/Filter"
import personServices from './services/persons'

const App = () => {
  const [ persons, setPersons] = useState([]) 

  const[newName, setNewName] = useState(
    ""
  )

  const[newNumber, setNewNumber] = useState(
    ""
  )

  const [searchInput, setSearchInput] = useState("")

  useEffect(() => {
    personServices
      .getAll()
        .then(initialPersons => {
        setPersons(initialPersons)
        })

  }, [])

  const addPerson = (event) => {
    event.preventDefault()
    const person = {
      name: newName, number: newNumber
    }

    // Check if person's name exists in persons
    if (persons.map(person=>
      {return person.name}).includes(newName)){
        if (window.confirm(`Do you really want to delete ${person.name}`)) { 
          const old_person = persons.find(person=> person.name === newName)
          personServices
          .update(old_person.id, person)
          .then(returnedPerson =>
            setPersons(persons.map(person => (person.id === old_person.id) ? returnedPerson : person)))
        }
      } 


    else {
      personServices
      .create(person)
      .then(returnedPerson => {
      setPersons(persons.concat(returnedPerson))
      setNewName("")
      setNewNumber("")
    })
    } 
  }


  function arrayRemove(arr, value) { 

    return arr.filter(function(ele){ 
        return ele != value; 
    });
}

  const removePerson = (person) => {
  if (window.confirm(`Do you really want to delete ${person.name}`)) { 
      personServices.
      remove(person.id)
      .then(returnValue => {
        setPersons(arrayRemove(persons, person))
      })
    }
}

  const handleNameChange = (event) => {
    console.log(event.target.value)
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }

  const handleInputChange = (event) => {
    console.log(event.target.value)
    setSearchInput(event.target.value)
  }



  const personsToShow = 
    persons.filter(person=>person.name.toLowerCase().includes(searchInput.toLowerCase()))


  return (
    <div>
      <h2>Phonebook</h2>
        <div>
        Search with name: 
        <FilterPersons
        searchInput = {searchInput}
        handleInputChange = {handleInputChange}/>
          <h2>Add a new</h2>
          <AddPerson 
          newNumber = {newNumber} 
          newName = {newName}
          handleNameChange = {handleNameChange}
          handleNumberChange = {handleNumberChange}
          addPerson = {addPerson}
          />
        </div>
      <h2>Numbers</h2>
        <ul>
        <ShowPersons
        personsToShow = {personsToShow}
        removePerson = {removePerson}
        />
        </ul>
    </div>
  )

}

export default App
