import React, { useState } from 'react'
import AddPerson from "./components/AddPerson"

const App = () => {
  const [ persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ]) 

  const[newName, setNewName] = useState(
    ""
  )

  const[newNumber, setNewNumber] = useState(
    ""
  )
  const [searchInput, setSearchInput] = useState("")

  const addPerson = (event) => {
    event.preventDefault()
    const person = {
      name: newName, number: newNumber
    }
    // Check if person's name exists in persons
    if (persons.map(person=>
      {return person.name}).includes(newName)){
      alert(`Name ${newName} already exists in Phonebook`)
    } 
    else {
      setPersons(persons.concat(person))
      setNewName("")
      setNewNumber("")
    }
  } 


  const handleInputChange = (event) => {
    console.log(event.target.value)
    setSearchInput(event.target.value)
  }

  const personsToShow =
    persons.filter(person=>person.name.toLowerCase().includes(searchInput.toLowerCase()))


  return (
    <div>
      <h2>Phonebook</h2>
        <div>
        Search with name: 
        <input value = {searchInput}
        onChange = {handleInputChange}/>
          <h2>Add a new</h2>
          <AddPerson setNewNumber = {setNewNumber} setNewName = {setNewName}/>
        </div>
      <h2>Numbers</h2>
        <ul>
        {personsToShow.map(person =>
          <li key = {person.name}> {person.name} {person.number} </li>
        )}
        </ul>
    </div>
  )

}

export default App
