import React, { useState } from 'react'
import DisplayPersons from './components/Persons'
import FilterPersons from './components/Filter'
import AddPerson from './components/AddPerson'



const App = () => {

  axios
  .get('http://localhost:3001/persons/notes')
  .then(response => {
    const notes = response.data
    console.log(notes)
  })


  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ])

  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber ] = useState('')
  const [filter, setNewFilter] = useState('')

  const addPerson = event => {
    event.preventDefault()

    const person = {
        name: newName,
        number : newNumber
    }

    if (persons.find(person => person.name.toLowerCase() === newName.toLowerCase())) {
      alert(`${newName} is already added to phonebook`)
    } 
    else {
      setPersons(persons.concat(person))
      setNewName('')
      setNewNumber('')
    }
  }


  const filteredPersons = filter
  ? persons.filter(person => {
    return person.name.toLowerCase().includes(filter.toLowerCase())
  })
  : persons



  return (
    <div>
      <h2>Phonebook</h2>
      <FilterPersons setFilter = {setNewFilter} filter = {filter}/>
      <h2>Add new contact</h2>
      <AddPerson 
      addPerson = {addPerson}
      setNewName = {setNewName}
      newName = {newName}
      setNewNumber = {setNewNumber}
      newNumber = {newNumber}
      />  
      <h2>Contacts</h2>
      <DisplayPersons persons = {filteredPersons} />
    </div>
  )

}

export default App
