import React from 'react'


const Blog = ({blog, addLikes}) => {

  const handleLike = () => {
    addLikes({
      title: blog.title,
      author: blog.author,
      url: blog.url,
      likes: blog.likes + 1
    },
    blog.id)
  }

  return (
  <div>
    {blog.title} {blog.author} {blog.url} {blog.likes} <button onClick={handleLike}>Like</button>
  </div>  
  )
}

export default Blog
