import React, { useState } from 'react'
import blogService from '../services/blogs'


const BlogForm = ({ setBlogs, setNotificationMessage, blogRef}) => {
    const [title, setTitle] = useState("")
    const [author, setAuthor] = useState("")
    const [url, setUrl] = useState("")


    const titleChange = (value) => {
        setTitle(value)
    }

    const authorChange = (value) => {
        setAuthor(value)
    }

    const urlChange = (value) => {
        setUrl(value)
    }

    const handleBlogChange = async (e) => {
        e.preventDefault()
        try {

            const added_blog = await blogService
                .create( { title, author, url })

            console.log(added_blog)
            blogRef.current.toggleVisibility()
            setTitle('')
            setAuthor('')
            setUrl('')

            setNotificationMessage(`New blog: ${title} ${author} created`)

            blogService.getAll().then((blogs) => setBlogs(blogs))
            setTimeout(() => {
                setNotificationMessage(null)
            }, 5000)
        } catch (exception) {
            setNotificationMessage("Invalid syntax")
          setTimeout(() => {
            setNotificationMessage(null)
          }, 5000)
        }
      }

        return (
        <form onSubmit={handleBlogChange}>
            <div>
            title:
                <input
                id = "title"
                type="text"
                value={title}
                name="title"
                onChange={({ target }) => titleChange(target.value)}
            />
            </div>
            <div>
            author:
                <input
                id = "author"
                type="text"
                value={author}
                name="author"
                onChange={({ target }) => authorChange(target.value)}
            />
            </div>
            <div>
            url:
                <input
                id = "url"
                type="text"
                value={url}
                name="url"
                onChange={({ target }) => urlChange(target.value)}
            />
            </div>
            <button type="submit">add blog</button>
        </form>
        )
    }

