import React from 'react'
import { Link, useParams } from 'react-router-dom'
import { TableCell } from '@material-ui/core'
import blogService from '../services/blogs'


export const BlogView = ({ allBlogs, likeBlog }) => {
    const id = useParams().id
    const blog = allBlogs.find(b => b.id === id)

    const handleLike = () => {
      const { author, url, title } = blog
  
      const updatedBlog = 
      {
        id: blog.id,
        user: blog.user?.id || blog.user,
        likes: blog.likes + 1,  
        author: author,
        title: title,
        url: url
      }
      // console.log(id ,updatedBlog)
      likeBlog(updatedBlog)
    }

    const getBlogComments  = async (id) => {
      const comments = await blogService.getComments(id)
      return comments
    }

    const comments = getBlogComments(id)

    console.log(comments, "comments")

    return (
    <div>
      <div>
      <h2>{blog.title}</h2>
      <div>
      {blog.url}
      </div>
      </div>
      <div>  
      added by {blog.user.username} 
      </div>
      <div>
      {blog.likes} <button onClick={handleLike}>Like</button>
      </div>
      <h2>comments</h2>
      {comments !== null &&
          <ul>{
              comments.map(comment => {
                  return <li key={comment.id}>{comment.content}</li>
              })}
          </ul>
      }
    </div>  
    )
  }




const Blog = ({ blog, likeBlog, deleteBlog, user }) => {

  const handleLike = () => {
    const { author, url, title } = blog

    const updatedBlog = 
    {
      id: blog.id,
      user: blog.user?.id || blog.user,
      likes: blog.likes + 1,  
      author: author,
      title: title,
      url: url
    }
    // console.log(id ,updatedBlog)
    likeBlog(updatedBlog)
  }

  const handleDelete = () => {
    const id = blog.id
    console.log(id)
    deleteBlog(id)
  }

  return (
    <TableCell>
      <Link to={`/blogs/${blog.id}`}>
      {blog.title}
      </Link>
    </TableCell>
    /* {blog.author} 
    {blog.url} 
    {blog.likes} <button onClick={handleLike}>Like</button> <button onClick={handleDelete}>Delete</button> */
  )
}

export default Blog
