import React, { useState, useEffect} from 'react'
import Blog from './components/Blog'
import Notification from "./components/Notification"
import blogService from './services/blogs'
import loginService from './services/login'
import LoginForm from "./components/LoginForm"
import BlogForm from "./components/BlogForm"

import { useDispatch, useSelector } from 'react-redux'

import Togglable from './components/Togglable'

import { setNotification } from './reducers/notificationReducer'
import { initializeBlogs } from './reducers/blogReducer'
import { updateBlog } from './reducers/blogReducer'
import { deleteBlog } from './reducers/blogReducer'

const App = () => {
  const [username, setUsername] = useState('') 
  const [password, setPassword] = useState('')
  const [user, setUser] = useState(null)

  const blogs = useSelector(state => state.blogs)
  const dispatch = useDispatch()

  const addBlogRef = React.createRef();

  useEffect(() => {
    dispatch(initializeBlogs())
    }, [dispatch])

  console.log(blogs, "blogs")
  
  useEffect(() => {
    const loggedInUserJSON = window.localStorage.getItem('loggedUser');
    if (loggedInUserJSON) {
      const user = JSON.parse(loggedInUserJSON);
      setUser(user);
      blogService.setToken(user.token);
    }
  }, []);
  
  const handleLogin = async (event) => {
    event.preventDefault();
    
    try {
      const user = await loginService({
        username, password
      });
      console.log(user.id, "user.id")
      window.localStorage.setItem('loggedUser', JSON.stringify(user));
      blogService.setToken(user.token)
      setUser(user)
      setUsername('')
      setPassword('')
      dispatch(setNotification(user.username + ' logged in', 5))
      console.log("Something logged in")
    } 
    catch (exception) {
      console.log(exception)
      dispatch(setNotification('wrong credentials', 5))
    }
  }
  

  const handleLogout = async () => {
    window.localStorage.clear()
    setUser(null);
  }



  const blogAddForm  = () => {
    return (
    <Togglable buttonText='Add Blog' ref={addBlogRef} >
      <BlogForm/>
      </Togglable> 
    )  
}

  const likeBlog = async (blogObject) => {
    try {  
      await dispatch(updateBlog(blogObject))
      dispatch(setNotification("Liked blog " + blogObject.title, 5))
      // dispatch(initializeBlogs())
    }
    catch (err) {
      console.error(err)
      dispatch(setNotification(err, 5))
    }
  }


  const deleteBlog = async (id) => {
    try {  
      await dispatch(deleteBlog(id))
      dispatch(setNotification("Liked blog " + blogObject.title, 5))
      // dispatch(initializeBlogs())
    }
    catch (err) {
      console.error(err)
      dispatch(setNotification(err, 5))
    }
  
  }



    return (
      <div>
        <h2>Welcome to blog app</h2>
        <Notification/>
          {user === null
          ? <LoginForm
            handleSubmit={handleLogin}
            username={username}
            password={password}
            setUsername={setUsername}
            setPassword={setPassword}
          />
          : <div>
          <p>
          <button onClick={handleLogout}> Log Out</button> 
          </p>
          <h2>blogs</h2>
          {blogAddForm()}
          { blogs
            .sort((a, b) => b.likes - a.likes)
            .map((blog) => (
            <Blog  
            key={blog.id}
            user={user}
            blog={blog} 
            likeBlog={likeBlog}
            deleteBlog={deleteBlog}
            />
          ))}
          </div>}        
         </div>
    )
  }

export default App
