import React from 'react'
import { Link, useParams } from 'react-router-dom'

export const BlogView = ({ allBlogs, likeBlog }) => {
    const id = useParams().id
    console.log(allBlogs, "all_blogs")
    const blog = allBlogs.find(b => b.id === id)

    console.log(blog, "blog")
    const handleLike = () => {
      const { author, url, title } = blog
  
      const updatedBlog = 
      {
        id: blog.id,
        user: blog.user?.id || blog.user,
        likes: blog.likes + 1,  
        author: author,
        title: title,
        url: url
      }
      // console.log(id ,updatedBlog)
      likeBlog(updatedBlog)
    }

    return (
    <div>
      {blog.title} 
      added by + {blog.user} 
      {blog.likes} <button onClick={handleLike}>Like</button>
    </div>  
    )
  }




const Blog = ({ blog, likeBlog, deleteBlog, user }) => {

  const handleLike = () => {
    const { author, url, title } = blog

    const updatedBlog = 
    {
      id: blog.id,
      user: blog.user?.id || blog.user,
      likes: blog.likes + 1,  
      author: author,
      title: title,
      url: url
    }
    // console.log(id ,updatedBlog)
    likeBlog(updatedBlog)
  }

  const handleDelete = () => {
    const id = blog.id
    console.log(id)
    deleteBlog(id)
  }

  return (
  <div>
    <Link to={`/blogs/${blog.id}`}>
    {blog.title}
    </Link>
    {blog.author} 
    {blog.url} 
    {blog.likes} <button onClick={handleLike}>Like</button> <button onClick={handleDelete}>Delete</button>
  </div>  
  )
}

export default Blog
