import React, { useState } from 'react'
import { useDispatch } from 'react-redux';
import { createBlog } from '../reducers/blogReducer'
import { setNotification } from '../reducers/notificationReducer'
import { connect } from 'react-redux'


const BlogForm = () => {
    const [title, setTitle] = useState("")
    const [author, setAuthor] = useState("")
    const [url, setUrl] = useState("")
    const dispatch = useDispatch()

    const titleChange = (value) => {
        setTitle(value)
    }

    const authorChange = (value) => {
        setAuthor(value)
    }

    const urlChange = (value) => {
        setUrl(value)
    }
  

    const createBlog = async (blogObject) => {
        addBlogRef.current.toggleVisibility()
        await dispatch(blogService
          .create(blogObject))
          dispatch(setNotification(`New blog: ${blogObject.title} ${blogObject.author} created`, 5))
          // setNotificationMessage(`New blog: ${blogObject.title} ${blogObject.author} created`)
          
      }
    

    const handleBlogChange = async (event) => {
        event.preventDefault()
        try {
            const blogObject =  { title, author, url }
            createBlog(blogObject)
            
            setTitle('')
            setAuthor('')
            setUrl('')
            
            // setTimeout(() => {
            //     setNotificationMessage(null)
            // }, 5000)
        } catch (exception) {
            dispatch(setNotification("Invalid syntax", 5))
        //   setTimeout(() => {
        //     setNotificationMessage(null)
        //   }, 5000)
        }
      }

        return (
        <form onSubmit={handleBlogChange}>
            <div>
            title:
                <input
                id = "title"
                type="text"
                value={title}
                name="title"
                onChange={({ target }) => titleChange(target.value)}
            />
            </div>
            <div>
            author:
                <input
                id = "author"
                type="text"
                value={author}
                name="author"
                onChange={({ target }) => authorChange(target.value)}
            />
            </div>
            <div>
            url:
                <input
                id = "url"
                type="text"
                value={url}
                name="url"
                onChange={({ target }) => urlChange(target.value)}
            />
            </div>
            <button type="submit">add blog</button>
        </form>
        )
    }


// const ConnectedBlogForm = connect(null, { createBlog, setNotification })(BlogForm)
export default BlogForm
